import React, { useState } from 'react';
import Mo from './Main.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass, faTrash, faSquarePlus } from '@fortawesome/free-solid-svg-icons';
import Logo11 from './Components/Images/logo11.png';

function Main() {
  const [searchText, setSearchText] = useState('');
  const [newTask, setNewTask] = useState(''); // State to store the new task
  const [showTaskPopup, setShowTaskPopup] = useState(false); // State to control task input popup

  const [tasks, setTasks] = useState([
    { id: 1, text: 'Study React JS today' },
    { id: 2, text: 'Learn React JS today' },
    { id: 3, text: 'Understand React JS' },
    { id: 4, text: 'Practice React JS' },
    { id: 5, text: 'Workout React JS' },
  ]);

  const filteredTasks = tasks.filter((task) =>
    task.text.toLowerCase().includes(searchText.toLowerCase())
  );

  const handleSearchChange = (e) => {
    setSearchText(e.target.value);
  };

  const handleDeleteTask = (taskId) => {
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
  };

  const openTaskPopup = () => {
    setShowTaskPopup(true);
  };

  const closeTaskPopup = () => {
    setShowTaskPopup(false);
    setNewTask('');
  };

  const handleAddTask = () => {
    if (newTask.trim() === '') {
      alert('Please enter a task before adding.');
    } else {
      setTasks((prevTasks) => [
        ...prevTasks,
        { id: prevTasks.length + 1, text: newTask },
      ]);
      closeTaskPopup();
    }
  };

  const handleNewTaskInputChange = (e) => {
    setNewTask(e.target.value);
  };

  return (
    <div className={Mo.main1}>
      <div className={Mo.head}>
        <h1>To - Do</h1>
      </div>
      <div className={Mo.inputContainer}>
        <FontAwesomeIcon icon={faMagnifyingGlass} className={Mo.icn2} />
        <input
          type='text'
          className={Mo.inp1}
          placeholder='Search ToDo task here.'
          value={searchText}
          onChange={handleSearchChange}
        />
      </div>
      <div className={Mo.imgm}>
        <img src={Logo11} className={Mo.img11} alt="Logo" />
        <h3 className={Mo.info}>
        ''Organize your time for<br />more productive work.<br />Try to achieve your goals<br />on time by managing<br />via our app, Your goals<br />are the main priority for us. Start today. Let this<br />be day one for anything<br />and everything.''
        </h3>
      </div>
      <div className={Mo.taskmain}>
        <h2>Today's Tasks</h2>
        {filteredTasks.map((task) => (
          <div key={task.id} className={Mo.task}>
            <label htmlFor={`checkbox-${task.id}`} className={Mo.formControl}>
              <input type="checkbox" id={`checkbox-${task.id}`} name={`checkbox-${task.id}`} />
            </label>
            <h4 className={Mo.lab}>{task.text}</h4>
            <FontAwesomeIcon
              icon={faTrash}
              className={Mo.tra}
              onClick={() => handleDeleteTask(task.id)}
            />
          </div>
        ))}
        <div className={Mo.task}>
          <label htmlFor="newTask" className={Mo.formControl}>
            <input
              type="checkbox"
              id="newTask"
              name="newTask"
              disabled // Disable the checkbox for the new task
            />
          </label>
          <input
            type="text"
            className={`${Mo.newTaskInput} ${showTaskPopup ? Mo.showPopup : ''}`}
            placeholder='Add a new task...'
            value={newTask}
            onChange={handleNewTaskInputChange}
            onKeyUp={(e) => {
              if (e.key === 'Enter') {
                handleAddTask();
              }
            }}
          />
          <FontAwesomeIcon icon={faTrash} className={Mo.tra} />
        </div>
      </div>
      <div className={Mo.plus} onClick={openTaskPopup}>
        <FontAwesomeIcon icon={faSquarePlus} className={Mo.pl} />
      </div>
    </div>
  );
}

export default Main;
